import { View, Text, StyleSheet, ScrollView } from "react-native";
import {
  MagnifyingGlassIcon,
  AdjustmentsVerticalIcon,
  ArrowRightIcon,
} from "react-native-heroicons/outline";
import RestaurantCard from "./RestaurantCard";

const FeaturedRowComponent = (props) => {
  return (
    <View>
      <View
        style={styles.featuredHeader}
        className="mt-4 flex-row items-center justify-between px-4"
      >
        <Text className="font-bold text-lg">{props.title}</Text>
        <ArrowRightIcon color="#00CCBB" />
      </View>
      <Text style={styles.description} className="text-xs text-gray-500 px-4">
        {props.description}
      </Text>
      <ScrollView
        style={styles.contentContainer}
        horizontal
        showsHorizontalScrollIndicator={false}
        className="pt-4 px-4"
      >
        <RestaurantCard />
        <RestaurantCard />
        <RestaurantCard />
        <RestaurantCard />
        <RestaurantCard />
        <RestaurantCard />
        <RestaurantCard />
      </ScrollView>
    </View>
  );
};

const styles = StyleSheet.create({
  featuredHeader: {
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-between",
    marginTop: 16,
    paddingHorizontal: 16,
  },
  description: {
    fontSize: 12,
    paddingHorizontal: 16,
    color: "#6B7280",
  },
  contentContainer: {
    paddingTop: 16,
    paddingHorizontal: 16,
  },
});

export default FeaturedRowComponent;
