import { Image, Text, TouchableOpacity } from "react-native";

const CategoryCard = ({ title, imgUrl }) => {
  return (
    <TouchableOpacity>
      <Image source={{ uri: imgUrl }} className="h-20 rounded" />
      <Text>{title}</Text>
    </TouchableOpacity>
  );
};

export default CategoryCard;
