import { Image, Text, TouchableOpacity } from "react-native";

const CategoryCard = (props) => {
  return (
    <TouchableOpacity>
      <Image
        source={{
          uri: props.imgUrl,
        }}
        className="h-20 w-20 rounded"
      />
      <Text>{props.title}</Text>
    </TouchableOpacity>
  );
};

export default CategoryCard;
